import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { useEffect, useState } from "react";
import { Container, ListGroup, Button } from "react-bootstrap";
import { Buffer } from "buffer";
import axios from "axios";
import qs from "qs";

const CLIENT_ID = "72b562044db441c8ac5f234210896424";
const CLIENT_SECRET = "fc952f7f3aee40f4b0c16d41336b7486";

function App() {
  const [accessToken, setAccessToken] = useState("");
  const [topSongs, setTopSongs] = useState([]);

  useEffect(() => {
    const auth_token = Buffer.from(
      `${CLIENT_ID}:${CLIENT_SECRET}`,
      "utf-8"
    ).toString("base64");

    const data = qs.stringify({
      grant_type: "client_credentials",
      scopes: 'user-top-read'
    });

    axios
      .post("https://accounts.spotify.com/api/token", data, {
        headers: {
          Authorization: `Basic ${auth_token}`,
          "Content-Type": "application/x-www-form-urlencoded",
        },
      })
      .then(
        // response => console.log(response)
        (response) => setAccessToken(response.data.access_token)
      );


  }, []);

  const getTopSongs = async () => {
    // await axios({
    //   url: 'https://api.spotify.com/v1/me/top/tracks',
    //   method: "GET",
    //   headers: {
    //     'Content-Type': 'application/json',
    //     'Authorization': 'Bearer ' + accessToken
    //   }
    // }).then(
    //   response => {return console.log(response)}
    // );
    console.log(accessToken);
    const params = {
      method: "GET",
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + accessToken
      }
    }
    await fetch("https://api.spotify.com/v1/me/top/tracks", params).then(
      response => console.log(response)
    )
  }


  return (
    <div className="App">
      <Container>
        <Button onClick={getTopSongs}>
          test
        </Button>
        <ListGroup></ListGroup>
      </Container>
    </div>
  );
}

export default App;
