import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { useEffect, useState } from "react";
import { Container, ListGroup } from "react-bootstrap";
import { Buffer } from "buffer";
import axios from "axios";
import qs from "qs";

const CLIENT_ID = "72b562044db441c8ac5f234210896424";
const CLIENT_SECRET = "fc952f7f3aee40f4b0c16d41336b7486";

function App() {
  const [accessToken, setAccessToken] = useState("");
  const [topSongs, setTopSongs] = useState([]);

  useEffect(() => {
    const auth_token = Buffer.from(
      `${CLIENT_ID}:${CLIENT_SECRET}`,
      "utf-8"
    ).toString("base64");

    const data = qs.stringify({
      grant_type: "client_credentials",
    });

    axios
      .post("https://accounts.spotify.com/api/token", data, {
        headers: {
          Authorization: `Basic ${auth_token}`,
          "Content-Type": "application/x-www-form-urlencoded",
        },
      })
      .then(
        // response => console.log(response)
        (response) => setAccessToken(response.data.access_token)
      );

    axios.get("https://api.spotify.com/v1/me/top/tracks?time_range=short_term&limit=5", [], {
      headers: {
        Authorization: `Bearer ${accessToken}`
      }
    }).then(
      response => console.log(response)
    );
  }, []);

  return (
    <div className="App">
      <Container>
        <ListGroup></ListGroup>
      </Container>
    </div>
  );
}

export default App;
