import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { useEffect, useState } from "react";
import { Container, ListGroup } from "react-bootstrap";
import { Buffer } from "buffer";

const CLIENTID = "72b562044db441c8ac5f234210896424";
const CLIENTSECRET = "fc952f7f3aee40f4b0c16d41336b7486";

function App() {
  const [accessToken, setAccessToken] = useState("");
  const [topSongs, setTopSongs] = useState([]);

  useEffect(() => {
    const authParms = {
      method: "POST",
      headers: {
        Authorization:
          "Basic " +
          new Buffer.from(CLIENTID + ":" + CLIENTSECRET).toString("base64"),
        "Content-Type": "application/x-www-form-urlencoded",
      },
      body: {
        grant_type: "client_credentials",
      },
      json: true,
    };

    fetch("https://accounts.spotify.com/api/token", authParms).then((result) =>
      console.log(result)
    );
  }, []);

  return (
    <div className="App">
      <Container>
        <ListGroup></ListGroup>
      </Container>
    </div>
  );
}

export default App;
